import css from "./Profile.module.css"
import React, {PureComponent} from "react";
import Preloader from "../../common/preloader/preloader";
import {profileAPI} from "../../../ api/api";

const Post = (props) => {
    return <div className={css.Post}>
        {props.text}
    </div>
}

class ProfileStatus extends React.Component {

    componentDidUpdate(prevProps, prevState) {
        if (prevProps.status !== this.props.status) {
            this.setState({status: this.props.status})
        }
    }

    state = {
        editMode: false,
        status: this.props.status,
    }
    onStatusChange = (e) => {
        this.setState({status: e.currentTarget.value})
    }

    render() {
        return (<div>
            <div> {this.state.editMode
                ? <input onChange={this.onStatusChange} value={this.state.status} onBlur={() => {
                    this.setState({editMode: false});
                    this.props.updateStatus(this.state.status);
                }}/>
                : <div onClick={() => {
                    this.setState({editMode: true});
                }}>{this.props.status || "no status"}</ div>}
            </div>

        </div>)
    }
}

class Profile extends PureComponent {
    render() {
        let postElements = this.props.ProfileData.posts.map(p => <Post text={p.text}/>);
        let text = React.createRef();

        if (!this.props.ProfileData.profile) {
            return <Preloader/>
        }

        return (<div className={css.Profile}>
                <div className={css.ProfileName}>{this.props.ProfileData.profile.fullName}</div>
                <img className={css.ProfilePicture}
                     src={this.props.ProfileData.profile.photos.large}/>
                <br/>

                <ProfileStatus status={this.props.ProfileData.status} updateStatus={this.props.updateStatus}/>

                <br/>
                <textarea onChange={() => this.props.textareaChange(text)}
                          ref={text}
                          value={this.props.ProfileData.newPostText.text}>
            </textarea>
                <br/>
                <button onClick={() => {
                    this.props.addPost();
                }}>Add new post
                </button>
                <br/>
                <div className={css.MyPosts}>
                    {postElements}
                </div>
            </div>
        );
    }
}

export default Profile;
