let initValue = {
    users: []
};
const usersReducer = (state = initValue, action) => {

    switch (action.type) {
        case "FOLLOW": {
            let stateCopy = {...state};
            stateCopy.users = [...state.users];
            stateCopy.users.map(u => {
                if (u.id == action.userId){
                    u.isFollowed = true;
                }
            })
            return state;
        }
        case "UNFOLLOW": {
            let stateCopy = {...state};
            stateCopy.users = [...state.users];
            stateCopy.users.map(u => {
                if (u.id == action.userId){
                    u.isFollowed = false
                }
            })
            return state;
        }
        case "SET-USERS": {
            return {...state, users: action.users}
        }
        default:
            return state;
    }
}

export default usersReducer;
