let UrlDataContainer = (props) => {
    let userId = Object.values(useParams())[0];
    return <ProfileAPI {...props} userId={userId}/>
}

export default compose(
    connect(mapStateToProps, {
        textareaChange,
        addPost,
        setUserProfile,
        getProfileThunkCreator
    }),
    withAuthRedirect,
)(UrlDataContainer);
