import {NavLink, Navigate, Route, Routes} from "react-router-dom";
import css from "./Messages.module.css"
import React, {useState} from "react";


const DialogItem = (props) => {
    return <div className={css.DialogItem}>
        <NavLink onClick={()=>{props.setActiveDialog(props.name)}} to={"/messages/" + props.name}> {props.name} </NavLink>
    </div>
}
const MessageItem = (props) => {
    return <div className={css.MessageItem}>
        {props.message}
    </div>
}

const Messages = (props) => {
    console.log(props);


    let text = React.createRef();
    const dialogList = props.messageData.friends.map(d => <DialogItem setActiveDialog={props.setActiveDialog} id={d.id} name={d.name} key={d.id}/>);
    let messageList = props.messageData.dialogs.map(m => <Route
        path={m.name}
        key={m.name}
        element={m.messages.map(ml => <MessageItem message={ml.message} key={ml.message}/>)}/>);

    return <div className={css.Messages}>
        <div className={css.LeftColumn}>
            {dialogList}
        </div>
        <div className={css.RightColumn}>
            <Routes>
                {messageList}
            </Routes>
            <textarea ref={text}
                      value={props.messageData.newMessageText.message}
                      onChange={() => props.textareaChange(text)}/>
            <br/>
            <button onClick={() => {
                props.addMessage()
            }}> send
            </button>
        </div>
    </div>


