import css from "./Profile.module.css"
import React from "react";
import Preloader from "../../common/preloader/preloader";
import {profileAPI} from "../../../ api/api";

const Post = (props) => {
    return <div className={css.Post}>
        {props.text}
    </div>
}

class ProfileStatus extends React.Component {
    componentDidMount() {
        window.jopa = this.state;
    }

    state = {
        editMode: false,
        status: this.props.status,
    }
    onStatusChange = (e) => {
        this.setState({status: e.currentTarget.value})
    }

    render() {
        return (<div>
            <div> {this.state.editMode
                ? <input onChange={this.onStatusChange} value={this.state.status} onBlur={() => {
                    this.setState({editMode: false});
                    this.props.updateStatus(this.state.status);
                }}/>
                : <div onClick={() => {
                    this.setState({editMode: true});
                }}>{this.state.status}</ div>}
            </div>

            <button onClick={()=>{
                //profileAPI.updateStatus(this.state.status).then(response => {console.log(response)});
                console.log(this.props.updateStatus);
                console.log(profileAPI.updateStatus())
            }
            }> jopa </button>

        </div>)
    }
}

const Profile = (props) => {
    let postElements = props.ProfileData.posts.map(p => <Post text={p.text}/>);
    let text = React.createRef();

    if (!props.ProfileData.profile) {
        return <Preloader/>
    }

    return (<div className={css.Profile}>
            <div className={css.ProfileName}>{props.ProfileData.profile.fullName}</div>
            <img className={css.ProfilePicture}
                 src={props.ProfileData.profile.photos.large}/>
            <br/>

            <ProfileStatus status={props.ProfileData.status} updateStatus={props.getStatusThunkCreator}/>

            <br/>
            <textarea onChange={() => props.textareaChange(text)}
                      ref={text}
                      value={props.ProfileData.newPostText.text}>
            </textarea>
            <br/>
            <button onClick={() => {
                props.addPost();
            }}>Add new post
            </button>
            <br/>
            <div className={css.MyPosts}>
                {postElements}
            </div>
        </div>
    );
}

export default Profile;
