import css from "./Profile.module.css"
import React from "react";

const Post = (props) => {
    return <div className={css.Post}>
        {props.text}
    </div>
}


const Profile = (props) => {
    let postElements = props.postData.map(p => <Post text={p.text}/>)
    let textareaChange =()=>{

    };
    let addPost = props.addPost;

    let text = React.createRef();


    return (<div className={css.Profile}>
            <div className={css.ProfileName}>Имя Фамилия</div>
            <img className={css.ProfilePicture}
                 src="https://www.meme-arsenal.com/memes/f5dc681ed9feb4c5f4a4516e73572bb1.jpg"/>
            <br/>
            <textarea onChange={textareaChange}
                      ref={text}>
            </textarea>
            <br/>
            <button onClick={() => props.addPost(text.current.value)}>Add new post</button>
            <br/>
            <div className={css.MyPosts}>
                {postElements}
            </div>
        </div>
    );
}

export default Profile;
