import React, {useState} from "react";
import css from "./Users.module.css"
import {NavLink} from "react-router-dom";

let Paginator =(props) =>{
    let pageSize = props.pageInfo.pageSize
    let pagesCount = Math.ceil(props.pageInfo.pageCount / props.pageInfo.pageSize);
    let pages = [];
    for (let i = 1; i <= pagesCount; i++) {
        pages.push(i);
    }
    let portionCount = Math.ceil(pagesCount/pageSize);
    let [portionNumber, setPortionNumber] = useState(1);
    let leftPortionNumber = (portionNumber - 1) * portionSize + 1;
    pages = pages.map(p => <span
        className={props.pageInfo.currentPage === p && css.SelectedPage}
        onClick={() => props.onPageChange(p)}> {p} </span>);
    return <div> {pages} </div>
}

let Users = (props) => {

    let jopaU = (userId) => {
        props.unfollowThunkCreator(userId);
    }
    let jopaF = (userId) => {
        props.followThunkCreator(userId);
    }

    return (<div>
        <Paginator {...props}/>
        {
            props.users.map(u => {
                return (
                    <div>
                        <NavLink to={"/profile/" + u.id}>
                            {u.name}
                            <br/>
                            <img src={u.photos.small}/>
                        </NavLink>
                        <br/>
                        {
                            u.followed
                                ? <button disabled={props.followingInProgress.some(id => id === u.id)}
                                          onClick={() => jopaU(u.id)}> unfollow </button>
                                : <button disabled={props.followingInProgress.some(id => id === u.id)}
                                          onClick={() => jopaF(u.id)}> follow </button>
                        }
                    </div>)
            })
        }
        <br/>
        <button onClick={props.getUsers}> get users</button>
    </div>)
}


export default Users;
