import css from "./Profile.module.css"
import React, {PureComponent, useEffect, useState} from "react";
import Preloader from "../../common/preloader/preloader";

const Post = (props) => {
    return <div className={css.Post}>
        {props.text}
    </div>
}

class ProfileStatus extends PureComponent {
    state = {
        editMode: false, status: this.props.status,
    }

    componentDidUpdate(prevProps, prevState) {
        if (prevProps.status !== this.props.status) {
            this.setState({status: this.props.status})
        }
    }

    onStatusChange = (e) => {
        this.setState({status: e.currentTarget.value})
    }

    render() {
        return (<div>
            <div> {this.state.editMode ? <input onChange={this.onStatusChange} value={this.state.status} onBlur={() => {
                this.setState({editMode: false});
                this.props.updateStatus(this.state.status);
            }}/> : <div onClick={() => {
                this.setState({editMode: true});
            }}>{this.props.status || "no status"}</ div>}
            </div>
        </div>)
    }
}

const Profile = (props) => {
    let postElements = props.ProfileData.posts.map(p => <Post text={p.text} key={p.text}/>);
    let text = React.createRef();
    const [editMode, setEditMode] = useState(false);
    const [localProfile, setLocalProfile] = useState({});
    useEffect(() => {
        setLocalProfile({...props.ProfileData.profile});
    }, [props.ProfileData.profile])

    if (!props.ProfileData.profile) {
        return <Preloader/>
    }
    window.avatar = document.getElementById('avatar').files[0];
    return (<div className={css.Profile}>
        <div className={css.Name}>{props.ProfileData.profile.fullName}</div>
        <ProfileStatus status={props.ProfileData.status} updateStatus={props.updateStatus}/>
        <img className={css.ProfilePicture}
             src={props.ProfileData.profile.photos.large ? props.ProfileData.profile.photos.large : "https://cdn.pixabay.com/photo/2015/10/05/22/37/blank-profile-picture-973460_960_720.png"}/>
        {editMode
            ? <input type={"file"} id={"avatar"}> change picture </input>
            : null
        }
        {/*about me*/}
        <div className={css.AboutMe}>
            <div style={{display: "inline"}}>About me:</div>
            {editMode
                ? <input style={{display: "inline"}} onChange={(event) => {
                    setLocalProfile({...localProfile, aboutMe: event.currentTarget.value})
                }
                } type={"text"} value={localProfile.aboutMe || ""}/>
                : <div style={{display: "inline"}}>{localProfile.aboutMe ? localProfile.aboutMe : " no info"}</div>}
        </div>
        {/*looking for a job*/}
        <div className={css.LookingForAJob}>
            <div style={{display: "inline"}}>Looking for a Job:</div>
            {editMode
                ? <input onChange={() => {
                    setLocalProfile({...localProfile, lookingForAJob: !localProfile.lookingForAJob, lookingForAJobDescription:"nothing here"})
                }} type={"checkbox"} checked={localProfile.lookingForAJob}/>
                : <div style={{display: "inline"}}>{localProfile.lookingForAJob ? "yes" : "no"}</div>}
        </div>
        {/*{vk}*/}
        <div className={css.Contacts}>
            <div style={{display: "inline"}}>vk:</div>
            {editMode
                ? <input style={{display: "inline"}} onChange={(event) => {
                    setLocalProfile({
                        ...localProfile,
                        contacts: {...localProfile.contacts, vk: event.currentTarget.value}
                    })
                }
                } type={"text"} value={localProfile.contacts.vk || ""}/>
                : <div
                    style={{display: "inline"}}>{localProfile.contacts ? localProfile.contacts.vk ? localProfile.contacts.vk : " no info" : " no info"}</div>}
        </div>
        {/*github*/}
        <div className={css.Contacts}>
            <div style={{display: "inline"}}>github:</div>
            {editMode
                ? <input style={{display: "inline"}} onChange={(event) => {
                    setLocalProfile({
                        ...localProfile,
                        contacts: {...localProfile.contacts, github: event.currentTarget.value}
                    })
                }
                } type={"text"} value={localProfile.contacts.github || ""}/>
                : <div
                    style={{display: "inline"}}>{localProfile.contacts ? localProfile.contacts.github ? localProfile.contacts.github : " no info" : " no info"}</div>}
        </div>
        {/*website*/}
        <div className={css.Contacts}>
            <div style={{display: "inline"}}>website:</div>
            {editMode
                ? <input style={{display: "inline"}} onChange={(event) => {
                    setLocalProfile({
                        ...localProfile,
                        contacts: {...localProfile.contacts, website: event.currentTarget.value}
                    })
                }
                } type={"text"} value={localProfile.contacts.website || ""}/>
                : <div
                    style={{display: "inline"}}>{localProfile.contacts ? localProfile.contacts.website ? localProfile.contacts.website : " no info" : " no info"}</div>}
        </div>

        {(props.myId === props.userId) ? <button onClick={() => {
            if (editMode) props.updateProfileThunkCreator(localProfile);
            setEditMode(!editMode);
        }}> {editMode ? "submit changes" : "change profile info"} </button> : null}
    </div>);


    // return (
    //     <div className={css.Profile}>
    //         <div className={css.ProfileName}>{props.ProfileData.profile.fullName}</div>
    //         <img className={css.ProfilePicture}
    //              src={props.ProfileData.profile.photos.large}/>
    //         <br/>
    //         <div>
    //             {props.isOwner && <input type={"file"}/>}
    //         </div>
    //         <ProfileStatus status={props.ProfileData.status} updateStatus={props.updateStatus}/>
    //         <br/>
    //         <textarea onChange={() => props.textareaChange(text)}
    //                   ref={text}
    //                   value={props.ProfileData.newPostText.text}>
    //         </textarea>
    //         <br/>
    //         <button onClick={() => {
    //             props.addPost();
    //         }}>Add new post
    //         </button>
    //         <br/>
    //         <div className={css.MyPosts}>
    //             {postElements}
    //         </div>
    //     </div>);
}
export default Profile;
