import React, {PureComponent} from "react";
import Users from "./Users";
import Preloader from "../../common/preloader/preloader";

class UsersAPI extends PureComponent {
    shouldComponentUpdate(nextProps, nextState, nextContext) {
        return false;
    }

    componentDidMount() {
       this.props.getUsersThunkCreator(this.props.pageInfo.currentPage, this.props.pageInfo.pageSize);
    }

    onPageChange = (pageNumber) => {
        this.props.getUsersThunkCreator(pageNumber, this.props.pageInfo.pageSize);
    }

    render() {
        console.log("rendered users")
        return this.props.pageInfo.isLoading ? <Preloader/> :
            <Users {...this.props} onPageChange={this.onPageChange}/>
    }
}

export default UsersAPI;
