import css from "./Profile.module.css"
import React, {PureComponent, useEffect, useState} from "react";
import Preloader from "../../common/preloader/preloader";
import {updateAvatarThunkCreator} from "../../../Redux/profileReducer";

const Post = (props) => {
    return <div className={css.Post}>
        {props.text}
    </div>
}

class ProfileStatus extends PureComponent {
    state = {
        editMode: false, status: this.props.status,
    }

    componentDidUpdate(prevProps, prevState) {
        if (prevProps.status !== this.props.status) {
            this.setState({status: this.props.status})
        }
    }

    onStatusChange = (e) => {
        this.setState({status: e.currentTarget.value})
    }

    render() {
        return (<div>
            <div> {this.state.editMode ? <input onChange={this.onStatusChange} value={this.state.status} onBlur={() => {
                this.setState({editMode: false});
                this.props.updateStatus(this.state.status);
            }}/> : <div onClick={() => {
                this.setState({editMode: true});
            }}>{this.props.status || "no status"}</ div>}
            </div>
        </div>)
    }
}

const Profile = (props) => {
    let postElements = props.ProfileData.posts.map(p => <Post text={p.text} key={p.text}/>);
    let text = React.createRef();
    const [editMode, setEditMode] = useState(false);
    const [localProfile, setLocalProfile] = useState({});
    useEffect(() => {
        setLocalProfile({...props.ProfileData.profile});
    }, [props.ProfileData.profile])

    if (!props.ProfileData.profile) {
        return <Preloader/>
    }
    return (<div className={css.Profile}>

        <div className={css.Columns}>
            <div className={css.LeftColumn}>
                <img className={css.ProfilePicture}
                     src={props.ProfileData.profile.photos.large ? props.ProfileData.profile.photos.large : "https://cdn.pixabay.com/photo/2015/10/05/22/37/blank-profile-picture-973460_960_720.png"}/>
                {editMode
                    ? <div>Изменить аватар: <input type={"file"} id={"avatar"} onChange={() => {
                        let avatar = document.getElementById('avatar').files[0];
                        props.updateAvatarThunkCreator(avatar);
                    }}/></div>
                    : null
                }
                <div className={css.UnderPicture}>
                    {(props.myId === props.userId)
                        ? <div>
                            <input className={css.AvatarInput} type={"file"} id={"avatar"} onChange={() => {
                                let avatar = document.getElementById('avatar').files[0];
                                props.updateAvatarThunkCreator(avatar);
                            }}/>
                            <button htmlFor={"avatar"}>k</button>
                            
                            <button className={css.UserButton} onClick={() => {
                        if (editMode) props.updateProfileThunkCreator(localProfile);
                        setEditMode(!editMode);
                    }}> {editMode
                            ? <img width={"40px"} src="https://cdn-icons.flaticon.com/png/512/3936/premium/3936639.png?token=exp=1648263547~hmac=d4cddbcd86d77b51b85d1fa6760fadec"/>
                            : <img width={"40px"} src="https://cdn-icons.flaticon.com/png/512/563/premium/563541.png?token=exp=1648263398~hmac=50f43df3f65158af62f8eaea3c6d5c90" />}</button>
                        </div>
                        : <div>
                            <button className={css.UserButton}> <img width={"40px"} src={"https://cdn-icons.flaticon.com/png/512/3683/premium/3683218.png?token=exp=1648263834~hmac=18ed54428d70bb7e945935fb3345e452"}/></button>
                            <button className={css.UserButton}> <img width={"40px"} src="https://cdn-icons.flaticon.com/png/512/3034/premium/3034023.png?token=exp=1648263796~hmac=d00bac341cbbbc957ff6db26d310bf55"/> </button>
                        </div>}
                </div>

            </div>

            <div className={css.RightColumn}>
                <div><span className={css.Name}>{props.ProfileData.profile.fullName}</span></div>
                <div className={css.Status}><ProfileStatus status={props.ProfileData.status}
                                                           updateStatus={props.updateStatus}/></div>

                <div className={css.Info}>
                    {/*about me*/}
                    <div className={css.AboutMe}>
                        <div style={{display: "inline"}}>About me:</div>
                        {editMode
                            ? <input style={{display: "inline"}} onChange={(event) => {
                                setLocalProfile({...localProfile, aboutMe: event.currentTarget.value})
                            }
                            } type={"text"} value={localProfile.aboutMe || ""}/>
                            : <div
                                style={{display: "inline"}}>{localProfile.aboutMe ? <span>{localProfile.aboutMe}</span> : <span>no info</span> }</div>}
                    </div>
                    {/*looking for a job*/}
                    <div className={css.LookingForAJob}>
                        <div style={{display: "inline"}}>Looking for a Job:</div>
                        {editMode
                            ? <input onChange={() => {
                                setLocalProfile({
                                    ...localProfile,
                                    lookingForAJob: !localProfile.lookingForAJob,
                                    lookingForAJobDescription: "nothing here"
                                })
                            }} type={"checkbox"} checked={localProfile.lookingForAJob}/>
                            : <div style={{display: "inline"}}>{localProfile.lookingForAJob ? <span>yes</span> : <span>no</span>}</div>}
                    </div>
                    {/*{vk}*/}
                    <div className={css.Contacts}>
                        <div style={{display: "inline"}}>{"vk: "}</div>
                        {editMode
                            ? <input style={{display: "inline"}} onChange={(event) => {
                                setLocalProfile({
                                    ...localProfile,
                                    contacts: {...localProfile.contacts, vk: event.currentTarget.value}
                                })
                            }
                            } type={"text"} value={localProfile.contacts.vk || ""}/>
                            : <div
                                style={{display: "inline"}}>{localProfile.contacts ? localProfile.contacts.vk ? <span>{localProfile.contacts.vk}</span> : <span>no info</span> : <span>no info</span>}</div>}
                    </div>
                    {/*github*/}
                    <div className={css.Contacts}>
                        <div style={{display: "inline"}}>{"github: "}</div>
                        {editMode
                            ? <input style={{display: "inline"}} onChange={(event) => {
                                setLocalProfile({
                                    ...localProfile,
                                    contacts: {...localProfile.contacts, github: event.currentTarget.value}
                                })
                            }
                            } type={"text"} value={localProfile.contacts.github || ""}/>
                            : <div
                                style={{display: "inline"}}>{localProfile.contacts ? localProfile.contacts.github ? <span>{localProfile.contacts.github}</span> : <span>no info</span> : <span>no info</span>}</div>}
                    </div>
                    {/*website*/}
                    <div className={css.Contacts}>
                        <div style={{display: "inline"}}>{"website: "}</div>
                        {editMode
                            ? <input style={{display: "inline"}} onChange={(event) => {
                                setLocalProfile({
                                    ...localProfile,
                                    contacts: {...localProfile.contacts, website: event.currentTarget.value}
                                })
                            }
                            } type={"text"} value={localProfile.contacts.website || ""}/>
                            : <div
                                style={{display: "inline"}}>{localProfile.contacts ? localProfile.contacts.website ? <span>{localProfile.contacts.website}</span> : <span>no info</span> : <span>no info</span>}</div>}
                    </div>
                </div>
            </div>
        </div>


        <br/>
        <textarea onChange={() => props.textareaChange(text)}
                  ref={text}
                  value={props.ProfileData.newPostText.text}>
        </textarea>
        <br/>
        <button onClick={() => {
            props.addPost();
        }}>Add new post
        </button>
        <br/>
        <div className={css.MyPosts}>
            {postElements}
        </div>
    </div>);


