import React from "react";


const Users = (props) => {

    if(props.users.length==0){
        props.setUsers({id: 0, isFollowed: true, name: "dmitry", status: "1 hundred bucks...", location: "jopa"},
            {id: 1, isFollowed: false, name: "stepa", status: "2 hundred bucks...", location: "jopa"},
            {id: 2, isFollowed: false, name: "petya", status: "3 hundred bucks...", location: "jopa"},
            {id: 3, isFollowed: true, name: "govena", status: "4 hundred bucks...", location: "jopa"});
    }

    return <div>{
        props.users.map(u => {
            return (
                <div>
                    {u.name}
                    <br/>
                    {
                        u.isFollowed
                            ? <button onClick={()=>props.unfollow(u.id)}> unfollow </button>
                            : <button onClick={()=>props.follow(u.id)}> follow </button>
                    }
                </div>)
        })
    }
    </div>
}

export default Users;
