import React from "react";
import axios from "axios";
import Users from "./UsersF";
import Preloader from "../../common/preloader/preloader";

class UsersAPI extends React.Component {

    componentDidMount() {
        this.props.isLoading(true);
        axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${this.props.pageInfo.currentPage}&count=${this.props.pageInfo.pageSize}`,
            {withCredentials:true}).then(response => {
            this.props.setUsers(response.data.items);
            this.props.isLoading(false);
        })
    }

    onPageChange = (pageNumber) => {
        this.props.setCurrentPage(pageNumber);
        axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${pageNumber}&count=${this.props.pageInfo.pageSize}`, {
            withCredentials:true}).then(response => {
            this.props.setUsers(response.data.items);})
    }

    render() {
        return this.props.pageInfo.isLoading ? <Preloader/>:
            <Users pageInfo={this.props.pageInfo}
                   follow={this.props.follow}
                   unfollow={this.props.unfollow}
                   users={this.props.users}
                   onPageChange={this.onPageChange}
                   setUsers = {this.props.setUsers}
                   setCurrentPage={this.props.setCurrentPage}
            />}
}
export default UsersAPI;
