import css from "./Profile.module.css"
import React, {PureComponent, useEffect, useState} from "react";
import Preloader from "../../common/preloader/preloader";

const Post = (props) => {
    return <div className={css.Post}>
        {props.text}
    </div>
}

class ProfileStatus extends PureComponent {
    state = {
        editMode: false, status: this.props.status,
    }

    componentDidUpdate(prevProps, prevState) {
        if (prevProps.status !== this.props.status) {
            this.setState({status: this.props.status})
        }
    }

    onStatusChange = (e) => {
        this.setState({status: e.currentTarget.value})
    }

    render() {
        return (<div>
            <div> {this.state.editMode ? <input onChange={this.onStatusChange} value={this.state.status} onBlur={() => {
                this.setState({editMode: false});
                this.props.updateStatus(this.state.status);
            }}/> : <div onClick={() => {
                this.setState({editMode: true});
            }}>{this.props.status || "no status"}</ div>}
            </div>

        </div>)
    }
}

const Profile = (props) => {
    let postElements = props.ProfileData.posts.map(p => <Post text={p.text} key={p.text}/>);
    let text = React.createRef();

    const [localProfile, setLocalProfile] = useState();
    useEffect(()=>{
        console.log(props.ProfileData.profile)
        setLocalProfile(props.ProfileData.profile);
        console.log(localProfile);
        window.jopse1 = props.ProfileData.profile;
        window.jopse2 = localProfile;
    },[props.ProfileData.profile])

    if (!props.ProfileData.profile) {
        return <Preloader/>
    }
    return (
        <div className={css.Profile}>
            <div className={css.Name}>{props.ProfileData.profile.fullName}</div>

            <div className={css.AboutMe}>
                {"About me: "}
                {props.ProfileData.profile.aboutMe ? props.ProfileData.profile.aboutMe : "no info"}
            </div>

            <div className={css.Name}>{props.ProfileData.profile.lookingForAJob}
                {"Looking for a job: "}
                {props.ProfileData.profile.lookingForAJob ? "Yes" : "No"}
            </div>

            <div className={css.Name}>
                {props.ProfileData.profile.lookingForAJobDescription}
            </div>
        </div>
    );
    // return (
    //     <div className={css.Profile}>
    //         <div className={css.ProfileName}>{props.ProfileData.profile.fullName}</div>
    //         <img className={css.ProfilePicture}
    //              src={props.ProfileData.profile.photos.large}/>
    //         <br/>
    //         <div>
    //             {props.isOwner && <input type={"file"}/>}
    //         </div>
    //         <ProfileStatus status={props.ProfileData.status} updateStatus={props.updateStatus}/>
    //         <br/>
    //         <textarea onChange={() => props.textareaChange(text)}
    //                   ref={text}
    //                   value={props.ProfileData.newPostText.text}>
    //         </textarea>
    //         <br/>
    //         <button onClick={() => {
    //             props.addPost();
    //         }}>Add new post
    //         </button>
    //         <br/>
    //         <div className={css.MyPosts}>
    //             {postElements}
    //         </div>
    //     </div>);
}
export default Profile;
