const profileReducer = (state, action) => {

    let addPost = () => {
        state.postData.posts.push(Object.assign({}, state.postData.newPostText));
        postTextChange("");
    };
    let postTextChange = (newText) => {
        state.postData.newPostText.text = newText;
    };

    if (action.type == "ADD-POST") {
        state.addPost();
    } else if (action.type == "CHANGE-POST") {
        state.postTextChange(action.text);
    } else console.log("unexpected action value, current value: " + action.type);

    return state;
}

export default profileReducer();
