let rerenderEntireTree =()=>{};

let state = {
    dialogData: [
        {id: "1", name: "user1"},
        {id: "2", name: "user2"},
        {id: "3", name: "user3"},
        {id: "4", name: "user4"},
        {id: "5", name: "user5"},

    ],
    messageData: [
        {id: "1", text: "Popa"},
        {id: "2", text: "Popa2"},
        {id: "3", text: "Popa3"},
        {id: "4", text: "Popa4"}
    ],
    postData: {
        posts: [
            {id: "1", likesCount: 3, text: "jopa"},
            {id: "2", likesCount: 6, text: "jopaa"},
            {id: "3", likesCount: 2, text: "jopaaa"},
            {id: "4", likesCount: 1, text: "jopaaaaaa"}
        ],
        newPostText: {id: "5", likesCount: 0, text: "asdasda"}
    }
};

let subscriber=(observer)=>{
    rerenderEntireTree = observer;
}

export let addPost = () => {
    state.postData.posts.push(state.postData.newPostText);
    postTextChange("");
    rerenderEntireTree();
}

export let postTextChange = (newText) => {
    state.postData.newPostText.text = newText;
    rerenderEntireTree();
}
export default state;
