import React from "react";
import axios from "axios";
import Users from "./Users";
import preloader from "../../../Spinner-1s-200px.gif"


class UsersAPI extends React.Component {

    componentDidMount() {
        axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${this.props.pageInfo.currentPage}&count=${this.props.pageInfo.pageSize}`).then(response => {
            this.props.setUsers(response.data.items);
        })
    }

    getUsers = () => {
        axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${this.props.pageInfo.currentPage}&count=${this.props.pageInfo.pageSize}`).then(response => {
            this.props.setUsers(response.data.items);
        })
    }

    render() {
        return <>
            {this.props.pageInfo.isLoading ? <img src={preloader} /> : null}
            <Users pageInfo={this.props.pageInfo}
                   follow={this.props.follow}
                   unfollow={this.props.unfollow}
                   users={this.props.users}
                   getUsers={this.getUsers}
                   setCurrentPage={this.props.setCurrentPage}/>
        </>
    }
}

export default UsersAPI;
