let initValue = {
    id: null,
    email: null,
    login: null,
};
const authReducer = (state = initValue, action) => {

    switch (action.type) {
        case "SET-AUTH": {
            return {
                ...state,
                ...action.data
            }
        }
        default:
            return state;
    }
}

export default authReducer;
