let initValue = {
    loginData: {
        id: null,
        email: null,
        login: null,
    }
};
const authReducer = (state = initValue, action) => {

    switch (action.type) {
        case "SET-AUTH": {
            let StateCopy;
            StateCopy = {...state};
            StateCopy.loginData = action.loginData;
            StateCopy.loginData.id = action.loginData.id;
            console.log(action.loginData);
            return StateCopy;
        }
        default:
            return state;
    }
}

export default authReducer;
