import {NavLink, Redirect, Route, Routes} from "react-router-dom";
import css from "./Messages.module.css"
import React from "react";
import Login from "../../Login/Login";


const DialogItem = (props) => {
    return <div className={css.DialogItem}>
        <NavLink to={"/messages/" + props.dialogName}> {props.dialogName} </NavLink>
    </div>
}
const MessageItem = (props) => {
    return <div className={css.MessageItem}>
        {props.message}
    </div>
}

const Messages = (props) => {
    let messageList;
    let dialogList = props.messageData.dialogs.map(d => <DialogItem dialogName={d.name}/>);
    let messages = props.messageData.dialogs.map(m => <Route
        path={m.name}
        element ={messageList = m.messages.map(ml => <MessageItem message={ml.message}/>)}>
    </Route>);

    let text = React.createRef();
    if (props.isAuth == false) return  (
        <Routes>
            <Route path={"/login/*"} element={<Login/>}/>}
        </Routes>
    );
    return (
        <div className={css.Messages}>

            <div className={css.leftbar}>
                {dialogList}
            </div>

            <div className={css.rightBar}>
                <Routes>
                    {messages}
                </Routes>
                <textarea ref={text}
                          value={props.messageData.newMessageText.message}
                          onChange={()=>props.textareaChange(text)}></textarea>
                <br/>
                <button onClick={()=>{props.addMessage()}}> send </button>

            </div>
        </div>
    );
}

export default Messages;
