import React, {useEffect} from "react";
import Messages from "./Messages";
import {connect} from "react-redux";
import {withAuthRedirect} from "../../../HOCs/WIthAuthRedirect";
import {compose} from "redux";
import {usersAPI} from "../../../ api/api";
import {useParams} from "react-router";

let mapStateToProps = (state) => {
    return {
        messageData: state.messageData,
        login:state.auth.login,
    }
}
let mapDispatchToProps = (dispatch) => {
    return {
        addMessage() {
            debugger;
            dispatch({type: "ADD-MESSAGE"});
        },
        textareaChange(text) {
            dispatch({type: "CHANGE-MESSAGE", text: text.current.value});
        },
        setDialogs(){
            dispatch({type: "SET-DIALOGS"})
        },
        getFriends() {
            usersAPI.getFriends().then(data => {
                dispatch({type: "SET-DIALOGS"})
                dispatch({type: "SET-FRIENDS", friends: data.items});
            });
        },
        setActiveDialog(dialogName){
          dispatch({type:"SET-ACTIVE-DIALOG", dialogName:dialogName})
        }
    }
}

let MessageAPI = (props) => {
    useEffect(() => {
        props.getFriends()
    }, [])
    let userId = Object.values(useParams())[0];
    return <Messages activeDialog={userId} {...props}/>
}


export default compose(
    connect(mapStateToProps, mapDispatchToProps),
    withAuthRedirect
)(MessageAPI);
