let initValue = {
    users: [{id: 0, isFollowed: true, name: "dmitry", status: "1 hundred bucks...", location: "jopa"},
        {id: 1, isFollowed: false, name: "stepa", status: "2 hundred bucks...", location: "jopa"},
        {id: 2, isFollowed: false, name: "petya", status: "3 hundred bucks...", location: "jopa"},
        {id: 3, isFollowed: true, name: "govena", status: "4 hundred bucks...", location: "jopa"}]
};
const usersReducer = (state = initValue, action) => {

    switch (action.type) {
        case "FOLLOW": {
            let stateCopy = {...state};
            stateCopy.users = [...state.users];
            stateCopy.users.map(u => {
                if (u.id == action.userId){
                    u.isFollowed = true;
                }
            })
            return stateCopy;
        }
        case "UNFOLLOW": {
            let stateCopy = {...state};
            stateCopy.users = [...state.users];
            stateCopy.users.map(u => {
                if (u.id == action.userId){
                    u.isFollowed = false
                }
            })
            return stateCopy;
        }
        case "SET-USERS": {
            return {...state, users: action.users}
        }
        default:
            return state;
    }
}

export default usersReducer;
