import React from "react";
import css from "./Users.module.css"
import {NavLink} from "react-router-dom";
import axios from "axios";


let jopa = (userId) =>{
    axios.post(`https://social-network.samuraijs.com/api/1.0/follow/${userId}`,{},{withCredentials:true})
        .then(response => {
            console.log(response);
        })
}

let Users = (props) => {
    let pagesCount = Math.ceil(props.pageInfo.pageCount / props.pageInfo.pageSize);
    let pages = [];
    for (let i = 1; i <= pagesCount; i++) {
        pages.push(i);
    }
    pages = pages.map(p => <span
        className={props.pageInfo.currentPage === p && css.SelectedPage}
        onClick={() => {
            props.setCurrentPage(p);
            props.getUsers();
        }
        }> {p} </span>);

    return (<div>
        <div> {pages}</div>
        {
            props.users.map(u => {
                return (
                   <div>
                       <NavLink to={"/profile/" + u.id}>
                           {u.name}
                           <br/>
                           <img src={u.photos.small}/>
                       </NavLink>
                       <br/>
                        {
                            u.followed
                                ? <button onClick={() => {
                                    props.unfollow(u.id);
                                    jopa(u.id);
                                }}> unfollow </button>
                                : <button onClick={() => props.follow(u.id)}> follow </button>
                        }
                    </div>)
            })
        }
        <br/>
        <button onClick={props.getUsers}> get users</button>
    </div>)
}


export default Users;
