import React from "react";
import Messages from "./Messages";
import {connect} from "react-redux";

const MessagesContainer = (props) => {

    let addMessage = () => {
        props.dispatch({type: "ADD-MESSAGE"});
    }
    let textareaChange = (text) => {
        props.dispatch({type: "CHANGE-MESSAGE", text: text.current.value});
    }
    return (<Messages addMessage={addMessage} textareaChange={textareaChange} messageData={props.messageData}/>);
}

let mapStateToProps = (state) => {
    return {
        dialogsPage: state.messageData
    }
}
let mapDispatchToProps = (dispatch) => {
    return {
        addMessage() {
            dispatch({type: "ADD-MESSAGE"});
        },
        textareaChange(text) {
            dispatch({type: "CHANGE-MESSAGE", text: text.current.value});
        }
    }
}
const ssss = connect(mapStateToProps, mapDispatchToProps)(Messages)

export default MessagesContainer;
