let initValue = {
        users: [],
        pageInfo: {
            pageCount: 15,
            pageSize: 4,
            currentPage:1,
            isLoading:true,
        }
    }
;
const usersReducer = (state = initValue, action) => {

    switch (action.type) {
        case "FOLLOW": {
            let stateCopy = {...state};
            stateCopy.users = [...state.users];
            stateCopy.users.map(u => {
                if (u.id == action.userId) {
                    u.followed = true;
                }
            })
            return stateCopy;
        }
        case "UNFOLLOW": {
            let stateCopy = {...state};
            stateCopy.users = [...state.users];
            stateCopy.users.map(u => {
                if (u.id == action.userId) {
                    u.followed = false
                }
            })
            return stateCopy;
        }case "SET-CURRENT-PAGE": {
            let stateCopy = {...state};
            stateCopy.pageInfo = {...state.pageInfo};
            stateCopy.pageInfo.currentPage = action.currentPage;
            return stateCopy;
        }
        case "SET-USERS": {
            return {...state, users: action.users}
        }
        case "IS-LOADING": {
            let stateCopy = {...state};
            stateCopy.pageInfo = {...state.pageInfo};
            stateCopy.pageInfo.isLoading = action.isLoading;
            return stateCopy;
        }
        default:
            return state;
    }
}

export default usersReducer;
