import React, {useEffect} from "react";
import Messages from "./Messages";
import {connect} from "react-redux";
import {withAuthRedirect} from "../../../HOCs/WIthAuthRedirect";
import {compose} from "redux";
import {getFriendsThunkCreator} from "../../../Redux/message-reducer";
import {usersAPI} from "../../../ api/api";

let mapStateToProps = (state) => {
    return {
        followedUsers: state.usersData.followedUsers,
        messageData: state.messageData,
    }
}
const setFriends = (friends) => { console.log("s"); return {type: "SET-FRIENDS", friends: friends}};
let mapDispatchToProps = (dispatch) => {
    return {
        addMessage() {
            dispatch({type: "ADD-MESSAGE"});
        },
        textareaChange(text) {
            dispatch({type: "CHANGE-MESSAGE", text: text.current.value});
        },
        getFriends() {
            usersAPI.getFriends().then(data => {
                if (data.resultCode === 0) {
                    console.log(data);
                    dispatch(setFriends(data));
                }
            });
        },
    }
}

let MessageAPI =(props) => {
    useEffect(()=>{props.getFriends()}, [])
    return <Messages {...props}/>
}

export default compose(
    connect(mapStateToProps, mapDispatchToProps),
    withAuthRedirect
)(MessageAPI);
