import {ErrorMessage, Formik, Form, Field} from "formik";
import {connect} from "react-redux";
import {loginThunkCreator} from "../../Redux/auth-reducer";
import {Navigate} from "react-router-dom";
import React from "react";

let mapStateToProps = (state) => {
    return {
        isAuth: state.auth.loggedIn,
    }
}

const Login = (props) => {

    const initialValues = {
        email: 'oxotnik3010@gmail.com',
        password: 'asnaebos',
    }
    const validate = (values) => {
        let errors = {};
        if (!values.email) {
            errors.email = "Required field";
        }
        if (!values.password) {
            errors.password = "Required field";
        }
        return errors;
    }
    const onSubmit = (values, {setStatus}) =>{
        setStatus("jopa");
        console.log(setStatus);
        props.loginThunkCreator(values.email, values.password, values.rememberMe);
    }

    if (props.isAuth) return <Navigate replace to="/profile"/>
    else return (
        <Formik onSubmit={onSubmit} initialValues={initialValues} validate={validate}>
            {({
                  values,
                  errors,
                  touched,
                  handleChange,
                  handleBlur,
                  handleSubmit,
                  isSubmitting,
                  status
              }) => (
                <Form>
                    <div>{status}</div>
                    <label htmlFor={'email'}>Email</label>
                    <br/>
                    <Field type={'email'} id={'email'} name={'email'}/>
                    <ErrorMessage name={"email"}/>
                    <br/>

                    <label htmlFor={'password'}>Password</label>
                    <br/>
                    <Field type={'password'} id={'password'} name={'password'}/>
                    <ErrorMessage name={"password"}/>
                    <br/>
                    <button type='submit'> Submit</button>
                </Form>
            )}
        </Formik>
    );










    // <div>
    //     <Formik onSubmit={onSubmit} initialValues={initialValues} validate={validate}>
    //     <Form>
    //         <label htmlFor={'email'}>Email</label>
    //         <br/>
    //         <Field type={'email'} id={'email'} name={'email'}/>
    //         <ErrorMessage name={"email"}/>
    //         <br/>
    //
    //         <label htmlFor={'password'}>Password</label>
    //         <br/>
    //         <Field type={'password'} id={'password'} name={'password'}/>
    //         <ErrorMessage name={"password"}/>
    //         <br/>
    //         <button type='submit'> Submit</button>
    //     </Form>
    //     </Formik>
    // </div>
};

export default connect(mapStateToProps, {loginThunkCreator})(Login);
