const messageReducer = (state, action) => {

    let addMessage = () => {
        state.dialogs[0].messages.push(Object.assign({}, state.newMessageText));
    };
    let messageTextChange = (newText) => {
        state.newMessageText.message = newText;
    };

    switch (action.type) {
        case "ADD-MESSAGE":
            addMessage();
            break;
        case "CHANGE-MESSAGE":
            messageTextChange(action.text);
            break;
        default:
            console.log("unexpected action value, current value: " + action.type);
            break;
    }

    return state;
}

export default messageReducer;
