import React from "react";
import axios from "axios";
import css from "./Users.module.css"


class Users extends React.Component {

    componentDidMount() {
        axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${this.props.pageInfo.currentPage}&count=${this.props.pageInfo.pageSize}`).then(response => {
            this.props.setUsers(response.data.items);
        })
    }

    getUsers = () => {
        axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${this.props.pageInfo.currentPage}&count=${this.props.pageInfo.pageSize}`).then(response => {
            this.props.setUsers(response.data.items);
        })
    }

    render() {

        let pagesCount = Math.ceil(this.props.pageInfo.pageCount / this.props.pageInfo.pageSize);
        let pages = [];
        for (let i = 1; i <= pagesCount; i++) {
            pages.push(i);
        }
        console.log(pages);
        pages = pages.map(p => <span
            className={this.props.pageInfo.currentPage === p && css.SelectedPage}
            onClick={() => {
                this.props.setCurrentPage(p);
                this.getUsers();
            }
            }> {p} </span>);

        return <div>
            <div> {pages}</div>
            {
                this.props.users.map(u => {
                    return (
                        <div>
                            {u.name}
                            <br/>
                            {
                                u.isFollowed
                                    ? <button onClick={() => this.props.unfollow(u.id)}> unfollow </button>
                                    : <button onClick={() => this.props.follow(u.id)}> follow </button>
                            }
                        </div>)
                })
            }
            <br/>
            <button onClick={this.getUsers}> get users</button>
        </div>
    }
}

export default Users;
