import Header from "./Header";
import React from "react";
import axios from "axios";
import {connect} from "react-redux";
import {setAuth, setAuthThunkCreator, logoutThunkCreator} from "../../Redux/auth-reducer";

class HeaderAPI extends React.Component {

    render() {
        return <Header {...this.props}/>
    }
}

const HeaderContainer = (props) =>{
    return <HeaderAPI {...props}/>
}
const mapStateToProps = (state) => {
    return {
        loggedIn: state.auth.loggedIn,
        login: state.auth.login,
    }
};

export default connect(mapStateToProps, {setAuth, setAuthThunkCreator, logoutThunkCreator})(HeaderContainer);
