import React from "react";
import Messages from "./Messages";

const MessagesContainer = (props) => {

    let addMessage = () => {
        props.dispatch({type:"ADD-MESSAGE"});
    }
    let textareaChange= (textarea) => {
        props.dispatch({type: "CHANGE-MESSAGE",text: textarea});
    }
    return (<Messages addMessage={addMessage} textareaChange={textareaChange} messageData={props.messageData} dispatch = {props.dispatch} />);
}

export default MessagesContainer;
