import React from "react";
import css from "./Users.module.css"
import {NavLink} from "react-router-dom";
import axios from "axios";
import {usersAPI} from "../../../ api/api";




let Users = (props) => {

    let jopaU = (userId) =>{
        debugger
        axios.delete(`https://social-network.samuraijs.com/api/1.0/follow/${userId}`,
            {
                headers: {"API-KEY":"0547c747-b993-4214-9f68-ed89a037b0b8"},
                withCredentials:true})
            .then(response => {
                if (response.data.resultCode===0){
                    props.unfollow(userId)
                    console.log("unfollowed")
                }
            })
    }
    let jopaF = (userId) =>{
        console.log("sendet follow request")
        usersAPI.sendFollowRequest(userId).then(data=>{
            if (data.resultCode===0){
                props.unfollow(userId)
                console.log("followed")
            }})
    }

    let pagesCount = Math.ceil(props.pageInfo.pageCount / props.pageInfo.pageSize);
    let pages = [];
    for (let i = 1; i <= pagesCount; i++) {
        pages.push(i);
    }
    pages = pages.map(p => <span
        className={props.pageInfo.currentPage === p && css.SelectedPage}
        onClick={() =>props.onPageChange(p)}> {p} </span>);

    return (<div>
        <div> {pages}</div>
        {
            props.users.map(u => {
                return (
                   <div>
                       <NavLink to={"/profile/" + u.id}>
                           {u.name}
                           <br/>
                           <img src={u.photos.small}/>
                       </NavLink>
                       <br/>
                        {
                            u.followed
                                ? <button onClick={() => jopaU(u.id)}> unfollow </button>
                                : <button onClick={() => jopaF(u.id)}> follow </button>
                        }
                    </div>)
            })
        }
        <br/>
        <button onClick={props.getUsers}> get users</button>
    </div>)
}


export default Users;
