import React from "react";

const UsersItem = (props) => {
    return (
        <div>
            {props.name}
            <br/>
        </div>
    );
}

const Users = (props) => {
    return <div>{
        props.users.map(u => {
            return (
                <div>
                    {u.name}
                    <br/>
                    {
                        u.isFollowed
                            ? <button onClick={props.unfollow(u.id)}> unfollow </button>
                            : <button> follow </button>
                    }
                </div>)
        })
    }
    </div>
}

export default Users;
