import {NavLink, Route, Routes} from "react-router-dom";
import css from "./Messages.module.css"


const DialogItem = (props) => {
    return <div className={css.DialogItem}>
        <NavLink to={"/messages/" + props.dialogName}> {props.dialogName} </NavLink>
    </div>
}
const MessageItem = (props) => {
    return <div className={css.MessageItem}>
        {props.message}
    </div>
}

const Messages = (props) => {
    let messageList;
    let dialogList = props.messageData.map(d => <DialogItem dialogName={d.name}/>);
    let messages = props.messageData.map(m => <Route
        path={m.name}
        element ={messageList = m.message.map(ml => <MessageItem message={ml.message}/>)}>
    </Route>);

    return (
        <div className={css.Messages}>

            <div className={css.leftbar}>
                {dialogList}
            </div>

            <div className={css.rightBar}>
                <Routes>
                    {messages}
                </Routes>
            </div>
        </div>
    );
}

export default Messages;
