import React from "react";
import ProfileAPI from "./ProfileAPI";
import {connect} from "react-redux";
import {withRouter} from "react-router-dom";

let mapStateToProps = (state) => {
    return {
        ProfileData: state.ProfileData
    }
}
let mapDispatchToProps = (dispatch) => {
    return {
        addPost() {
            dispatch({type:"ADD-POST"});
        },
        textareaChange(text) {
            dispatch({type: "CHANGE-POST",text: text.current.value});
        },
        setUserProfile(profile){
            dispatch({type:"SET-USER-PROFILE", profile:profile})
        }
    }
}

let UrlDataContainer = withRouter(ProfileAPI)

const ProfileContainer = connect(mapStateToProps, mapDispatchToProps)(UrlDataContainer)

export default ProfileContainer;
