import css from "./Profile.module.css"
import React, {PureComponent, useEffect, useState} from "react";
import Preloader from "../../common/preloader/preloader";

const Post = (props) => {
    return <div className={css.Post}>
        {props.text}
    </div>
}

class ProfileStatus extends PureComponent {
    state = {
        editMode: false, status: this.props.status,
    }

    componentDidUpdate(prevProps, prevState) {
        if (prevProps.status !== this.props.status) {
            this.setState({status: this.props.status})
        }
    }

    onStatusChange = (e) => {
        this.setState({status: e.currentTarget.value})
    }

    render() {
        return (<div>
            <div> {this.state.editMode ? <input onChange={this.onStatusChange} value={this.state.status} onBlur={() => {
                this.setState({editMode: false});
                this.props.updateStatus(this.state.status);
            }}/> : <div onClick={() => {
                this.setState({editMode: true});
            }}>{this.props.status || "no status"}</ div>}
            </div>

        </div>)
    }
}

const ProfileComponent = (props) => {
    return (
        <div className={css[props.componentName]}>
            <div style={{display: "inline"}}>{props.componentVisualName}:</div>
            {props.editMode
                ? <input style={{display: "inline"}} key={"props.componentName"} onChange={props.onComponentChange}
                         type={"text"} value={props.localProfile[props.componentName] || ""}/>
                : <div
                    style={{display: "inline"}}>{props.localProfile[props.componentName] ? props.localProfile[props.componentName] : " no info"}</div>}
        </div>)
}

const Profile = (props) => {

    let postElements = props.ProfileData.posts.map(p => <Post text={p.text} key={p.text}/>);
    let text = React.createRef();

    const [editMode, setEditMode] = useState(false);
    const [localProfile, setLocalProfile] = useState({});
    useEffect(() => {
        setLocalProfile({...props.ProfileData.profile});
    }, [props.ProfileData.profile])


    if (!props.ProfileData.profile) {
        return <Preloader/>
    }
    return (<div className={css.Profile}>
        <div className={css.Name}>{props.ProfileData.profile.fullName}</div>

        <div className={css.AboutMe}>
            <div style={{display: "inline"}}>About me:</div>
            {editMode
                ? <input style={{display: "inline"}} onChange={(event) => {
                    setLocalProfile({...localProfile, aboutMe: event.currentTarget.value})
                }
                } type={"text"} value={localProfile.aboutMe || ""}/>
                : <div style={{display: "inline"}}>{localProfile.aboutMe ? localProfile.aboutMe : " no info"}</div>}
        </div>

        <div className={css.LookingForAJob}>
            <div style={{display: "inline"}}>Looking for a Job:</div>
            {editMode
                ? <input onChange={() => {
                    setLocalProfile({...localProfile, lookingForAJob: !localProfile.lookingForAJob})
                }} type={"checkbox"} checked={localProfile.lookingForAJob}/>
                : <div style={{display: "inline"}}>{localProfile.lookingForAJob ? "yes" : "no"}</div>}
        </div>

        <div className={css.Contacts}>
            <div style={{display: "inline"}}>facebook:</div>
            {editMode
                ? <input style={{display: "inline"}} onChange={(event) => {
                    setLocalProfile({...localProfile, contacts: {facebook: event.currentTarget.value}})
                }
                } type={"text"} value={localProfile.contacts.facebook || ""}/>
                : <div style={{display: "inline"}}>{localProfile.contacts.facebook ? localProfile.contacts.facebook : " no info"}</div>}
        </div>

        <button onClick={() => {
            setEditMode(!editMode);
            console.log(localProfile);
            console.log(localProfile.contacts.facebook);

        }}> {editMode ? "submit changes" : "change profile info"} </button>
    </div>);


    // return (
    //     <div className={css.Profile}>
    //         <div className={css.ProfileName}>{props.ProfileData.profile.fullName}</div>
    //         <img className={css.ProfilePicture}
    //              src={props.ProfileData.profile.photos.large}/>
    //         <br/>
    //         <div>
    //             {props.isOwner && <input type={"file"}/>}
    //         </div>
    //         <ProfileStatus status={props.ProfileData.status} updateStatus={props.updateStatus}/>
    //         <br/>
    //         <textarea onChange={() => props.textareaChange(text)}
    //                   ref={text}
    //                   value={props.ProfileData.newPostText.text}>
    //         </textarea>
    //         <br/>
    //         <button onClick={() => {
    //             props.addPost();
    //         }}>Add new post
    //         </button>
    //         <br/>
    //         <div className={css.MyPosts}>
    //             {postElements}
    //         </div>
    //     </div>);
}
export default Profile;
