import profileReducer from "./profileReducer";
import messageReducer from "./message-reducer";
// import messageReducer from "./message-reducer";
export let store = {
    state: {
        messageData: {
            dialogs: [
                {
                    name: "stepa",
                    messages: [
                        {name: "user1", message: "sup brah"},
                        {name: "user1", message: "sup brahh"},
                        {name: "user1", message: "sup brahhhh"},
                        {name: "user1", message: "sup brahhhhhhhhh"}]
                },
                {
                    name: "pasha",
                    messages: [
                        {name: "user1", message: "supes brahes"},
                        {name: "user1", message: "sup brah"},
                        {name: "user1", message: "sup brah"},
                        {name: "user1", message: "sup brah"}
                    ]
                },
                {
                    name: "sanya",
                    messages: [
                        {name: "user1", message: "sup brah"},
                        {name: "user1", message: "sup brah"},
                        {name: "user1", message: "sup brah"},
                        {name: "user1", message: "sup brah"}]
                }
            ],
            newMessageText: {name: "user1", message: "shieeeet"},
        },
        postData: {
            posts: [
                {id: "1", likesCount: 3, text: "jopa"},
                {id: "2", likesCount: 6, text: "jopaa"},
                {id: "3", likesCount: 2, text: "jopaaa"},
                {id: "4", likesCount: 1, text: "jopaaaaaa"}
            ],
            newPostText: {id: "5", likesCount: 0, text: "asdasda"}
        }
    },

    rerenderEntireTree() {
    },
    subscriber(observer) {
        this.rerenderEntireTree = observer;
    },
    dispatch(action) {
        this.state.postData = profileReducer(this.state.postData, action);
        this.state.messageData = messageReducer(this.state.messageData, action)
        this.rerenderEntireTree();
    },

    getState() {
        return this.state;
    }

}
window.store = store;
export default store;
