import css from "./Messages.module.css";
import {NavLink, Route, Routes} from "react-router-dom";
import React from "react";
import Messages from "./Messages";

const DialogItem = (props) => {
    return <div className={css.DialogItem}>
        <NavLink to={"/messages/" + props.dialogName}> {props.dialogName} </NavLink>
    </div>
}
const MessageItem = (props) => {
    return <div className={css.MessageItem}>
        {props.message}
    </div>
}

const MessagesContainer = (props) => {
    let messageList;
    let dialogList = props.messageData.dialogs.map(d => <DialogItem dialogName={d.name}/>);
    let messages = props.messageData.dialogs.map(m => <Route
        path={m.name}
        element ={messageList = m.messages.map(ml => <MessageItem message={ml.message}/>)}>
    </Route>);

    let text = React.createRef();

    let addMessage = () => {
        props.dispatch({type:"ADD-MESSAGE"});
    }
    let textareaChange= () => {
        props.dispatch({type: "CHANGE-MESSAGE",text: text.current.value});
        text.current.value="";
    }
    return (<Messages addMessage={addMessage} textareaChange={textareaChange} messageData={props.messageData} />);
}

export default MessagesContainer;
