import React from "react";
import Profile from "./Profile";
import {connect} from "react-redux";
import Messages from "../Messages/Messages";

const ProfileContainer = (props) => {
    let addPost = () => {
        props.dispatch({type:"ADD-POST"});
    };
    let textareaChange = (text) => {
        props.dispatch({type: "CHANGE-POST",text: text.current.value});
    };

    return (<Profile addPost={addPost} textareaChange={textareaChange} postData={props.postData} />);
}

let mapStateToProps = (state) => {
    return {
        dialogsPage: state.messageData
    }
}
let mapDispatchToProps = (dispatch) => {
    return {
        addMessage() {
            dispatch({type: "ADD-MESSAGE"});
        },
        textareaChange(text) {
            dispatch({type: "CHANGE-MESSAGE", text: text.current.value});
        }
    }
}
const MessagesContainer = connect(mapStateToProps, mapDispatchToProps)(Messages)

export default ProfileContainer;
