const profileReducer = (state, action) => {

    let addPost = () => {
        state.posts.push(Object.assign({}, state.newPostText));
        postTextChange("");
    };
    let postTextChange = (newText) => {
        state.newPostText.text = newText;
    };

    switch (action.type) {
        case "ADD-POST":
            addPost();
            break;
        case "CHANGE-POST":
            postTextChange(action.text);
            break;
        default:
            console.log("unexpected action value, current value: " + action.type);
            break;
    }

    return state;
}

export default profileReducer;
