        follow(userId) {
           dispatch({type:"FOLLOW", userId:userId});
        },
        unfollow(userId){
            dispatch({type:"UNFOLLOW", userId:userId});
        },
        setUsers(users){
            dispatch({type:"SET-USERS", users:users});
        },
        setCurrentPage(currentPage){
            dispatch({type:"SET-CURRENT-PAGE", currentPage:currentPage});
        },
        isLoading(isLoading){
