import {useFormik, ErrorMessage, Formik, Form, Field} from "formik";
import {connect} from "react-redux";
import {loginThunkCreator} from "../../Redux/auth-reducer";
import {Navigate} from "react-router-dom";
import React from "react";

let mapStateToProps = (state) => {
    return {
        isAuth: state.auth.loggedIn,
    }
}

const Login = (props) => {

    let initialValues = {
        email: 'oxotnik3010@gmail.com',
        password: 'asnaebos',
    }
    let validationSchema = (values) => {
        let errors = {};
        if (!values.email) {
            errors.email = "Required field";
        }
        if (!values.password) {
            errors.password = "Required field";
        }
        return errors;
    }
    let onSubmit = (values) =>{
        props.loginThunkCreator(values.email, values.password, values.rememberMe);
    }

    const formik = useFormik({
        initialValues: initialValues,
        onSubmit: onSubmit,
        validate: validationSchema,
    })

    if (props.isAuth) return <Navigate replace to="/profile"/>
    else return <div>

        <Formik initialValues={initialValues} onSubmit={onSubmit} validationSchema={validationSchema}>
            <Form>
                <label htmlFor={'email'}>Email</label>
                <br/>
                <Field></Field>
                <br/>

                <label htmlFor={'password'}>Password</label>
                <br/>

            </Form>
        </Formik>


        <form onSubmit={formik.handleSubmit}>
            <label htmlFor={'email'}>Email</label>
            <br/>
            <input type={'email'} id={'email'} name={'email'}
                   onChange={formik.handleChange}
                   onBlur={formik.handleBlur}
                   value={formik.values.email}/>
            {formik.errors.email && formik.touched.email ? <div>{formik.errors.email}</div> : null}
            <br/>

            <label htmlFor={'password'}>Password</label>
            <br/>
            <input type={'password'} id={'password'} name={'password'}
                   onChange={formik.handleChange}
                   onBlur={formik.handleBlur}
                   value={formik.values.password}/>
            {formik.errors.password && formik.touched.password ? <div>{formik.errors.password}</div> : null}
            <br/>

            <button type='submit'> Submit</button>

        </form>
    </div>
};
export default connect(mapStateToProps, {loginThunkCreator})(Login);
