let initValue = {
    users: [
        {id: 1, isFollowed: true, name: "dmitry", status: "1 hundred bucks...", location: "jopa"},
        {id: 2, isFollowed: false, name: "stepa", status: "2 hundred bucks...", location: "jopa"},
        {id: 3, isFollowed: false, name: "petya", status: "3 hundred bucks...", location: "jopa"},
        {id: 4, isFollowed: true, name: "govena", status: "4 hundred bucks...", location: "jopa"}
    ]


};
const usersReducer = (state = initValue, action) => {

    switch (action.type) {
        case "FOLLOW": {
            return {
                ...state,
                users: state.users.map(us => {
                    if (us.id === action.userID) {
                        return ({...us, isFollowed: true});
                    }
                })
            }
        }
        case "UNFOLLOW": {
            return {
                ...state,
                users: state.users.map(u => {
                    if (u.id === action.userID) {
                        return ({...u, isFollowed: false});
                    }
                })
            }
        }
        case "SET-USERS":{
            return {...state, users: [...state.users, ...action.users]}
        }
        default:
            return state;
    }
}

export default usersReducer;
