const messageReducer = (state, action) => {

    let addMessage = () => {
        state.messageData.dialogs[0].messages.push(Object.assign({}, state.messageData.newMessageText));
    };
    let messageTextChange = (newText) => {
        state.messageData.newMessageText.message = newText;
    };

    if (action.type == "ADD-MESSAGE") {
        addMessage();
    } else if (action.type == "CHANGE-MESSAGE") {
        messageTextChange(action.text);
    } else console.log("unexpected action value, current value: " + action.type);

    return state;
}

export default messageReducer;
