import { BrowserRouter, Routes, Route } from "react-router-dom";
import Messages from "./Messages/Messages";
import Profile from "./Profile/Profile";

const Content = (props) => {
    window.contentState = props.store.getState()
  return (
      <Routes>
        <Route path="/messages/*" element={<Messages
            messageData = {props.store.getState().messageData.messageData}
            addMessage = {props.store.dispatch.bind(props.store)}
            messageTextChange = {props.store.dispatch.bind(props.store)}/>}/>
        <Route path="/profile/*" element={<Profile
            postData = {props.store.getState().postData}
            addPost={props.store.dispatch.bind(props.store)}
            postTextChange={props.store.dispatch.bind(props.store)}/>} />
      </Routes>
  );
}

export default Content;
