let initValue = {
    posts: [
        {id: "1", likesCount: 3, text: "jopa"},
        {id: "2", likesCount: 6, text: "jopaa"},
        {id: "3", likesCount: 2, text: "jopaaa"},
        {id: "4", likesCount: 1, text: "jopaaaaaa"}
    ],
    newPostText: {id: "5", likesCount: 0, text: "asdasda"}
};
const profileReducer = (state = initValue, action) => {

    switch (action.type) {
        case "ADD-POST": {
            let stateCopy = {...state};
            stateCopy.posts = [...state.posts]
            stateCopy.posts.push(Object.assign({}, state.newPostText));
            stateCopy.newPostText.text = '';
            return stateCopy;}
        case "CHANGE-POST": {
            let stateCopy = {...state};
            stateCopy.newPostText.text = action.text;
            return stateCopy;
        }
        default:
            return state;
    }
}

export default profileReducer;
