let initValue = {
        dialogs: [
            {
                name: "stepa",
                messages: [
                    {name: "user1", message: "sup brah"},
                    {name: "user1", message: "sup brahh"},
                    {name: "user1", message: "sup brahhhh"},
                    {name: "user1", message: "sup brahhhhhhhhh"}]
            },
            {
                name: "pasha",
                messages: [
                    {name: "user1", message: "supes brahes"},
                    {name: "user1", message: "sup brah"},
                    {name: "user1", message: "sup brah"},
                    {name: "user1", message: "sup brah"}
                ]
            },
            {
                name: "sanya",
                messages: [
                    {name: "user1", message: "sup brah"},
                    {name: "user1", message: "sup brah"},
                    {name: "user1", message: "sup brah"},
                    {name: "user1", message: "sup brah"}]
            }
        ],
        newMessageText: {name: "user1", message: "shieeeet"}
};
const messageReducer = (state = initValue, action) => {

    switch (action.type) {
        case "ADD-MESSAGE":{
            let stateCopy = {...state};
            stateCopy.dialogs = [...state.dialogs];
            stateCopy.newMessageText = {...state.newMessageText};
            stateCopy.dialogs[0].messages.push(Object.assign({}, stateCopy.newMessageText));
            stateCopy.newMessageText.message = "";
            return stateCopy;
        }
        case "CHANGE-MESSAGE":
            let stateCopy = {...state};
            stateCopy.dialogs = [...state.dialogs];
            stateCopy.newMessageText = {...state.newMessageText};
            stateCopy.newMessageText.message = action.text;
            return stateCopy;
        default:
            return state;
    }
}

export default messageReducer;
