import {useFormik, ErrorMessage, Formik, Form, Field} from "formik";
import {connect} from "react-redux";
import {loginThunkCreator} from "../../Redux/auth-reducer";
import {Navigate} from "react-router-dom";
import React from "react";

let mapStateToProps = (state) => {
    return {
        isAuth: state.auth.loggedIn,
    }
}

const Login = (props) => {

    let initialValues = {
        email: 'oxotnik3010@gmail.com',
        password: 'asnaebos',
    }
    let validationSchema = (values) => {
        let errors = {};
        if (!values.email) {
            errors.email = "Required field";
        }
        if (!values.password) {
            errors.password = "Required field";
        }
        return errors;
    }
    let onSubmit = (values) =>{
        props.loginThunkCreator(values.email, values.password, values.rememberMe);
    }


    if (props.isAuth) return <Navigate replace to="/profile"/>
    else return <div>

        {/*<Formik initialValues={initialValues} onSubmit={onSubmit} validationSchema={validationSchema}>*/}
        {/*    <Form>*/}
        {/*        <label htmlFor={'email'}>Email</label>*/}
        {/*        <br/>*/}
        {/*        <Field type={'email'} id={'email'} name={'email'}/>*/}
        {/*        <br/>*/}

        {/*        <label htmlFor={'password'}>Password</label>*/}
        {/*        <br/>*/}
        {/*        <Field type={'password'} id={'password'} name={'password'}/>*/}
        {/*        <br/>*/}
        {/*        <button type='submit'> Submit</button>*/}
        {/*    </Form>*/}
        {/*</Formik>*/}

        <Formik onSubmit={onSubmit} initialValues={initialValues} validationSchema={validationSchema}>
        <Form>
            <label htmlFor={'email'}>Email</label>
            <br/>
            <Field type={'email'} id={'email'} name={'email'}/>
            <ErrorMessage name={"email"}/>
            <br/>

            <label htmlFor={'password'}>Password</label>
            <br/>
            <Field type={'password'} id={'password'} name={'password'}/>
            <ErrorMessage name={"password"}/>
            <br/>
            <button type='submit'> Submit</button>
        </Form>
        </Formik>
    </div>
};
export default connect(mapStateToProps, {loginThunkCreator})(Login);
