import { NavLink } from "react-router-dom";
import css from "./Messages.module.css"


const DialogItem = (props) => {
  return <div className={css.DialogItem}>
    <NavLink to={"/messages/" + props.id}> {props.name} </NavLink>
  </div>
}
const MessageItem = (props) => {
  return <div className={css.MessageItem}>
    {props.text}
  </div>
}

const Messages = () => {
    let a = [{b:"jopa", c:"piz"}];
    let dialogData=[
        {id: "1", name:"user1"},
        {id: "2", name:"user2"},
        {id: "3", name:"user3"},
        {id: "4", name:"user4"},
        {id: "5", name:"user5"},
    ];
    let messageData=[
        {id: "1", text:"Popa"},
        {id: "2", text:"Popa2"},
        {id: "3", text:"Popa3"},
        {id: "4", text:"Popa4"}
    ];
    let dialogElements = dialogData.map(d => <DialogItem name={d.name} id={d.id} />)
    let messageElements = messageData.map(m => <MessageItem text={m.text} id={m.id} />)
  return (
    <div className={css.Messages}>

      <div className={css.leftbar}>
          {dialogElements}
      </div>

      <div className={css.rightBar}>
          {messageElements}
      </div>

    </div>
  );
}

export default Messages;
