import React, {useState} from "react";
import css from "./Users.module.css"
import {NavLink} from "react-router-dom";

let Paginator =(props) =>{
    let portionSize = 10;
    let pagesCount = Math.ceil(props.pageInfo.pageCount / props.pageInfo.pageSize);
    let pages = [];
    for (let i = 1; i <= pagesCount; i++) {
        pages.push(i);
    }
    let portionCount = Math.ceil(pagesCount/portionSize);
    let [portionNumber, setPortionNumber] = useState(1);
    let leftPortionNumber = (portionNumber - 1) * portionSize + 1;
    let rightPortionNumber = portionNumber * portionSize;
    pages = pages.filter(p => p >= leftPortionNumber && p<=rightPortionNumber).map(p => <span
        key = {p}
        className={props.pageInfo.currentPage === p ? css.SelectedPage:null}
        onClick={() => props.onPageChange(p)}> {p} </span>);
    return <div>
        <button onClick={() => {setPortionNumber(portionNumber-1)}}> back </button>
        {pages}
        <button onClick={() => {setPortionNumber(portionNumber+1)}}> next </button>
    </div>
}

let Users = (props) => {

    let unfollow = (userId) => {
        props.unfollowThunkCreator(userId);
    }
    let follow = (userId) => {
        props.followThunkCreator(userId);
    }

    return (<div>
        <Paginator {...props} />
        {
            props.users.map(u => {
                return (
                    <div key={u.name}>
                        <NavLink to={"/profile/" + u.id}>
                            {u.name}
                            <br/>
                            {u.photos.small ? <img src={u.photos.small}/> : <img style={{width:"100px"}} src={"https://cdn.pixabay.com/photo/2015/10/05/22/37/blank-profile-picture-973460_960_720.png"}/>}
                        </NavLink>
                        <br/>
                        {
                            u.followed
                                ? <button disabled={props.followingInProgress.some(id => id === u.id)}
                                          onClick={() => unfollow(u.id)}> unfollow </button>
                                : <button disabled={props.followingInProgress.some(id => id === u.id)}
                                          onClick={() => follow(u.id)}> follow </button>
                        }
                    </div>)
            })
        }
        <br/>
        <button onClick={props.getUsers}> get users</button>
    </div>)
}

export default Users;
