import React from "react";

const UsersItem =(props)=>{
    return (
        <div>
            {props.name}
            <br/>
            {this.props.followed ?
                <button> unfollow </button> : <button> follow </button>}
        </div>
    );
}

const Users=(props)=>{
    console.log(props);
    let users = props.users.map(u=>(<UsersItem name={u.name}/>));
    return <div> {users} </div>
}

export default Users;
